select countIf(occupied) as occupied, count(*) as total, (total - occupied) as free
from (
         SELECT sensor_name,
                argMax(kwh_supplied, timestamp) = 0 as occupied
         FROM sensors.charging_station
         where group_name = '${group_name_charging}' and timestamp <= now()
         group by sensor_name
         );

select countIf(occupied) as occupied, count(*) as total, (total - occupied) as free
from (
         SELECT sensor_name,
                argMax(is_occupied, timestamp) as occupied
         FROM sensors.parking
         where group_name = '${group_name_parking}' and timestamp <= now()
         group by sensor_name
         );
